    def parar_camera(self):
        self.semimagem()
        print("Parar")
        ti = 0
        name = 'CamOpen.exe'
        
        f = wmi.WMI()
        for process in f.win32_Process():
            if process.name == name:
                process.Terminate()
                print("Processo encontrado!! Encerrando...")
                ti +=1
                print("Encerrado!")

        if ti==0:
            print("Processo n√£o encontrado.")
    



